import { useContext, useState } from 'react'
import { Context } from '../context'

function Slider() {
    const { modalActive, setModalActive } = useContext(Context)
    const [one, setOne] = useState(true)
    const [two, setTwo] = useState(false)
    const [three, setThree] = useState(false)
    const [four, setFour] = useState(false)
    const [number, setNumber] = useState(1)

    const mainPhoto = {
        backgroundImage: `url(../images/image-product-${number}.jpg)`,
    }
    const handleOne = () => {
        setNumber(1)
        setOne(true)
        setTwo(false)
        setThree(false)
        setFour(false)
    }
    const handleTwo = () => {
        setNumber(2)
        setOne(false)
        setTwo(true)
        setThree(false)
        setFour(false)
    }
    const handleThree = () => {
        setNumber(3)
        setOne(false)
        setTwo(false)
        setThree(true)
        setFour(false)
    }
    const handleFour = () => {
        setNumber(4)
        setOne(false)
        setTwo(false)
        setThree(false)
        setFour(true)
    }
    return (
        <>
            <div className="left">
                <div className="main-photo" style={mainPhoto}></div>
                <div className="sub-photo">
                    <div className={one ? 'one act' : 'one'} onClick={handleOne}></div>
                    <div className={two ? 'two act' : 'two'} onClick={handleTwo}></div>
                    <div className={three ? 'three act' : 'three'} onClick={handleThree}></div>
                    <div className={four ? 'four act' : 'four'} onClick={handleFour}></div>
                </div>
            </div>
            <modal></modal>
        </>
    )
}

export { Slider }
