import { useContext } from 'react'
import { Context } from '../context'

function MobileMenu() {
    const { setMobileMenu, mobileMenu } = useContext(Context)

    const closeMenu = (event) => {
        setMobileMenu(false)
    }

    return (
        <div className="mobile-menu" onClick={closeMenu}>
            <div className="menu"></div>
        </div>
    )
}

export { MobileMenu }
