import { useContext } from 'react'
import { Context } from '../context'

function GoodCart() {
    const { activeCart, setActiveCart, count } = useContext(Context)

    return (
        <div className="good-cart">
            <div className="cart-header">Cart</div>
            <hr />
            {count === 0 ? (
                <div className="empty-cart">Your cart is empty.</div>
            ) : (
                <div className="not-empty-cart">
                    <div className="quantity">
                        <div className="quantity-logo"></div>
                        <div className="quantity-text"></div>
                        <div className="quantity-delete"></div>
                    </div>
                    <button className="checkout">Checkout</button>
                </div>
            )}
        </div>
    )
}

export { GoodCart }
