import { useContext } from 'react'
import { Context } from '../context'

function MobileMenu() {
    const { setMobileMenu, mobileMenu } = useContext(Context)

    const closeMenu = () => {
        setMobileMenu(false)
    }

    return (
        <div className="mobile-menu" onClick={closeMenu}>
            <div
                className="menu"
                onClick={(event) => {
                    event.stopPropagation()
                }}>
                <div className="navbar">
                    <p className="navbar-text">Collections</p>
                    <p className="navbar-text">Man</p>
                    <p className="navbar-text">Woman</p>
                    <p className="navbar-text">About</p>
                    <p className="navbar-text">Contacts</p>
                </div>
            </div>
        </div>
    )
}

export { MobileMenu }
