import imageProduct1Thumbnail from '../images/image-product-1-thumbnail.jpg'
import imageProduct1 from '../images/image-product-1.jpg'
import imageProduct2Thumbnail from '../images/image-product-2-thumbnail.jpg'
import imageProduct3Thumbnail from '../images/image-product-3-thumbnail.jpg'
import imageProduct4Thumbnail from '../images/image-product-4-thumbnail.jpg'

function Slider() {
    const mainPhoto = {
        backgroundImage: 'url(../images/image-product-1.jpg)',
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    }
    const subOne = {
        backgroundImage: 'url(../images/image-product-1-thumbnail.jpg)',
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    }
    const subTwo = {
        backgroundImage: 'url(../images/image-product-2-thumbnail.jpg)',
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    }
    const subThree = {
        backgroundImage: 'url(../images/image-product-3-thumbnail.jpg)',
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    }
    const subFour = {
        backgroundImage: 'url(../images/image-product-4-thumbnail.jpg)',
        backgroundSize: 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'center',
    }

    return (
        <div className="left">
            <div className="main-photo" style={mainPhoto}></div>
            <div className="sub-photo">
                <img className="first" src={imageProduct1Thumbnail} alt="" />
                <img className="first" src={imageProduct2Thumbnail} alt="" />
                <img className="first" src={imageProduct3Thumbnail} alt="" />
                <img className="first" src={imageProduct4Thumbnail} alt="" />
            </div>
        </div>
    )
}

export { Slider }
